customCSS:
/* Fix the player to the right at full height (minus the 4chan X header) */
#fcsp-container[data-view-style="playlist"] {
	height: auto !important;
	top: 0px !important;
	right: 0px !important;
	bottom: 24px !important;
	left: auto !important;
	height: auto !important;
	max-height: calc(100% - 24px);
	background: none !important;
	border: none !important;
}
/* I just prefer this. */
.fcsp-image-link {
	position: relative;
	overflow: hidden;
}
.fcsp-image-link:before {
	content: ' ';
	position: absolute;
	top: -20px;
	bottom: -20px;
	left: -20px;
	right: -20px;
	background-image: -moz-element(#fcspImage);
	background-size: cover;
	background-position-y: center;
	filter: blur(10px) brightness(1) saturate(.5);
	z-index: -1;
}
/* Hide things when the playlist is open */
#fcsp-container[data-view-style="playlist"] .fcsp-controls,
#fcsp-container[data-view-style="playlist"] .fcsp-hover-image,
#fcsp-container[data-view-style="playlist"] .fcsp-list-item .fcsp-item-menu-button,
#fcsp-container[data-view-style="playlist"] .fcsp-footer,
#fcsp-container[data-view-style="fullscreen"] .fcsp-row-thumb,
#fcsp-container[data-view-style="fullscreen"] .fcsp-row-image {
	display:none !important;
}
/* Header is shown with adjustments to handle the changed container style */
#fcsp-container[data-view-style="playlist"] .fcsp-header {
	position: relative !important;
	cursor: inherit;
	background: $config[colors.background];
	border-width: 0 1px 1px 0;
	transition: all .5s ease;
	opacity: .2
}
#fcsp-container[data-view-style="playlist"] .fcsp-header:hover {
	opacity: 1;
}
/* Hide the image with the playlist open, unless it's a video. *\/
#fcsp-container[data-view-style="playlist"]:not(.playing-animated) .fcsp-image-link {
	display: none !important;
}
/* Show videos in the playlist at the bottom right. *\/
#fcsp-container[data-view-style="playlist"].playing-animated .fcsp-image-link {
	justify-content: end;
}/**/

/* Chunky playlist items, with no background items and a squared thumnail image. */
#fcsp-container[data-view-style="playlist"] .fcsp-list-item {
	background: none !important;
	height: auto !important;
	text-align: right;
	align-items: center;
	transition: all .5s ease;
}
#fcsp-container[data-view-style="playlist"] .fcsp-row-thumb, #fcsp-container[data-view-style="playlist"] .fcsp-row-image {
	height: 3rem;
	width: 3rem;
	object-fit: cover;
	transition: all .5s ease;
}
/* Show a gradient background and increase the size of list items when you hover over them. */
#fcsp-container[data-view-style="playlist"] .fcsp-list-item:hover {
	font-size: 1rem;
	font-weight: bold;
	background-image: radial-gradient(circle at -50%, #0000 70%, $config[colors.odd_row]) !important;
}
#fcsp-container[data-view-style="playlist"] .fcsp-list-item:nth-child(2n):hover {
	background-image: radial-gradient(circle at -50%, #0000 70%, $config[colors.even_row]) !important;
}
#fcsp-container[data-view-style="playlist"] .fcsp-list-item:hover .fcsp-row-thumb, #fcsp-container[data-view-style="playlist"] .fcsp-row-image {
	height: 4rem;
	width: 4rem;
}
/* Add a gradient background to the playing item, make the text bigger, and style the text. */
#fcsp-container[data-view-style="playlist"] .fcsp-list-item.playing {
	background-image: radial-gradient(circle at -50%, #0000 70%, $config[colors.playing]) !important;
	text-shadow: 0 0 2px black;
	padding-left: 2px;
	font-weight: bold;
	font-size: 1rem;
	color: $config[colors.page_background];
}
/* Swap the thumb image with the full image for the playing item. */
#fcsp-container[data-view-style="playlist"] .fcsp-list-item:not(.playing) .fcsp-row-image {
	display: none;
}
#fcsp-container[data-view-style="playlist"] .fcsp-list-item.playing .fcsp-row-thumb {
	display: none;
}
/* Same gradient background style for dragging items. */
#fcsp-container[data-view-style="playlist"] .fcsp-dragging {
	background-image: radial-gradient(circle at -50%, #0000 70%, $config[colors.dragging]) !important
}

/* Add a resizer to the left of the header when the playlist is open. */
.playlist-resizer {
	width: .25rem;
	background-color: $config[colors.border];
	position: absolute;
	left: -.25rem;
	top: 0;
	bottom: 0;
	border-radius: 100% 0 0 100%;
	cursor: ew-resize;
	display: none;
	transition: all .5s ease;
}
.playlist-resizer:hover {
	width: .4rem;
	left: -.4rem;
}
#fcsp-container[data-view-style="playlist"] .playlist-resizer {
	display: block;
}
